import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import {Button, Form, Jumbotron, Modal} from 'react-bootstrap';
import {navigate, A} from 'hookrouter';
import axios from 'axios';

function AtualizarTarefa(props) {


    const API_URL_TAREFAS = 'http://localhost:3001/gerenciador-tarefas/';



    const [exibirModal, setExibirModal] = useState(false);
    const [formValidado, setFormValidado] = useState(false);
    const [tarefa, setTarefa] = useState('');
    const [carregarTarefa, setCarregarTarefa] = useState(true);
    const [exibirModalErro, setExibirModalErro] = useState(false);


    useEffect(()=>{
        async function obterTarefa(){
            try{
                let { data } = await axios.get(API_URL_TAREFAS+props.id);
                setTarefa(data.nome);
            }catch(err){
                navigate('/');
            }
        }
        if(carregarTarefa){
            obterTarefa();
            setCarregarTarefa(false);
        }

    }, [carregarTarefa, props]);



    function voltar(event){
        event.preventDefault();
        navigate('/');
    }

    function handleFecharModal(){
        navigate('/');
    }


    function handleFecharModalErro(){
        setExibirModalErro(false);
    }

    function atualizar2(event){
        event.preventDefault();
        setFormValidado(true);
        if(event.currentTarget.checkValidity() === true){
            //obter as tarefas
            const tarefasDb = localStorage['tarefas'];
            let tarefas = tarefasDb ? JSON.parse(tarefasDb) : [];
            //persistir a tarefa atualziada
            tarefas = tarefas.map(tarefaObj => {
                if(tarefaObj.id === parseInt(props.id)){
                    tarefaObj.nome = tarefa;
                }
                return tarefaObj;
            });
            localStorage['tarefas'] = JSON.stringify(tarefas);
            setExibirModal(true);
        }

    }

    function atualizar(event){
        event.preventDefault();
        setFormValidado(true);
        if(event.currentTarget.checkValidity() === true){
            //obter as tarefas
            const tarefasDb = localStorage['tarefas'];
            let tarefas = tarefasDb ? JSON.parse(tarefasDb) : [];
            //persistir a tarefa atualziada
            tarefas = tarefas.map(tarefaObj => {
                if(tarefaObj.id === parseInt(props.id)){
                    tarefaObj.nome = tarefa;
                }
                return tarefaObj;
            });
            localStorage['tarefas'] = JSON.stringify(tarefas);
            setExibirModal(true);
        }

    }

    function handleTxtTarefa(event){
        setTarefa(event.target.value);
        //o valor do txt
    }

    return (
    
        <div>
            <h3 className="text-center">Atualizar</h3>
            <Jumbotron>
                <Form onSubmit={atualizar} noValidate validated={formValidado}>
                    <Form.Group>
                        <Form.Label>Tarefa</Form.Label>
                        <Form.Control type="text" placeholder="Digite a tarefa"
                            minLength="5" maxLength="100" required data-testid="txt-tarefa" value={tarefa} onChange={handleTxtTarefa}>
                        </Form.Control>
                        <Form.Control.Feedback type="invalid">
                            A tarefa deve conter ao menos 5 caracteres
                        </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group className="text-center">
                        <Button variant="success" type="submit" data-testid="btn-atualizar">
                            Atualizar
                        </Button>
                        &nbsp;
                        <A href="/" className="btn btn-light" onClick={voltar}>
                            Voltar
                        </A>
                    </Form.Group>
                </Form>

                <Modal show={exibirModal} onHide={handleFecharModal} data-testid="modal">
                    <Modal.Header closeButton>
                        <Modal.Title>Sucesso </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Tarefa atualizado com sucesso!
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="success" onClick={handleFecharModal}>
                            Continuar
                        </Button>

                    </Modal.Footer>
                </Modal>

                <Modal show={exibirModalErro} onHide={handleFecharModalErro} data-testid="modal">
                    <Modal.Header closeButton>
                        <Modal.Title>Erro </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Erro ao atualizar tarefa, tente novamente em instantes.
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="warning" onClick={handleFecharModalErro}>
                            Fechar
                        </Button>

                    </Modal.Footer>
                </Modal>


            </Jumbotron>
        </div>
    );
}

AtualizarTarefa.propTypes={
    id: PropTypes.number.isRequired
}

export default AtualizarTarefa;
